member.example:
This C#/VB.NET code shows how to create and use GDI drawing engine 
            to draw different graphic objects like figures and text.
            
            Imports System.Drawing
            
            Imports Vintasoft.Imaging
            Imports Vintasoft.Imaging.Drawing
            Imports Vintasoft.Imaging.Drawing.Gdi
            
            Class GdiGraphicsExample
                ''' <summary>
                ''' Creates GDI Graphics drawing engine on specified image and performs drawing.
                ''' </summary>
                Public Shared Sub DrawWithGdiGraphics(image As VintasoftImage)
                    ' create drawing engine for image
                    Using drawingEngine As New GdiGraphics(image)
                        ' create drawing area
                        Dim drawingArea As New RectangleF(0, 0, image.Width, image.Height)
                        ' draw objects in area
                        DrawingExample(drawingEngine, drawingArea)
                    End Using
                End Sub
            
                ''' <summary>
                ''' Draws graphic objects with specified drawing engine.
                ''' </summary>
                ''' <param name="drawingEngine">Drawing engine.</param>
                ''' <param name="drawingArea">Image area to draw objects in.</param>
                Public Shared Sub DrawingExample(drawingEngine As DrawingEngine, drawingArea As RectangleF)
                    ' create bounding rectangle for ellipse
                    Dim ellipseRect As New RectangleF(drawingArea.X + drawingArea.Width * 0.3F, drawingArea.Y + drawingArea.Height * 0.3F, drawingArea.Width * 0.4F, drawingArea.Height * 0.4F)
            
                    ' create brush and pen to draw ellipse
                    Using brush As IDrawingSolidBrush = drawingEngine.DrawingFactory.CreateSolidBrush(Color.Blue)
                        Using pen As IDrawingPen = drawingEngine.DrawingFactory.CreatePen(Color.Red, 4F)
                            ' fill ellipse
                            drawingEngine.FillEllipse(brush, ellipseRect)
                            ' stroke ellipse
                            drawingEngine.DrawEllipse(pen, ellipseRect)
                        End Using
                    End Using
            
                    ' create brush and font to draw text
                    Using textBrush As IDrawingSolidBrush = drawingEngine.DrawingFactory.CreateSolidBrush(Color.Yellow)
                        Using font As IDrawingFont = drawingEngine.DrawingFactory.CreateFont("Arial", 30, True, False)
                            ' create properties to layout text
                            Dim layoutProperties As New TextLayoutProperties(AnchorType.Center)
                            ' draw text
                            drawingEngine.DrawText("Text", font, textBrush, ellipseRect, layoutProperties)
                        End Using
                    End Using
                End Sub
            End Class
            
            
            using System.Drawing;
            
            using Vintasoft.Imaging;
            using Vintasoft.Imaging.Drawing;
            using Vintasoft.Imaging.Drawing.Gdi;
            
            namespace CSHARP
            {
                class GdiGraphicsExample
                {
                    /// <summary>
                    /// Creates GDI Graphics drawing engine on specified image and performs drawing.
                    /// </summary>
                    public static void DrawWithGdiGraphics(VintasoftImage image)
                    {
                        // create drawing engine for image
                        using (GdiGraphics drawingEngine = new GdiGraphics(image))
                        {
                            // create drawing area
                            RectangleF drawingArea = new RectangleF(0, 0, image.Width, image.Height);
                            // draw objects in area
                            DrawingExample(drawingEngine, drawingArea);
                        }
                    }
            
                    /// <summary>
                    /// Draws graphic objects with specified drawing engine.
                    /// </summary>
                    /// <param name="drawingEngine">Drawing engine.</param>
                    /// <param name="drawingArea">Image area to draw objects in.</param>
                    public static void DrawingExample(DrawingEngine drawingEngine, RectangleF drawingArea)
                    {
                        // create bounding rectangle for ellipse
                        RectangleF ellipseRect = new RectangleF(
                            drawingArea.X + drawingArea.Width * 0.3f,
                            drawingArea.Y + drawingArea.Height * 0.3f,
                            drawingArea.Width * 0.4f,
                            drawingArea.Height * 0.4f);
            
                        // create brush and pen to draw ellipse
                        using (IDrawingSolidBrush brush = drawingEngine.DrawingFactory.CreateSolidBrush(Color.Blue))
                        {
                            using (IDrawingPen pen = drawingEngine.DrawingFactory.CreatePen(Color.Red, 4f))
                            {
                                // fill ellipse
                                drawingEngine.FillEllipse(brush, ellipseRect);
                                // stroke ellipse
                                drawingEngine.DrawEllipse(pen, ellipseRect);
                            }
                        }
            
                        // create brush and font to draw text
                        using (IDrawingSolidBrush textBrush = drawingEngine.DrawingFactory.CreateSolidBrush(Color.Yellow))
                        {
                            using (IDrawingFont font = drawingEngine.DrawingFactory.CreateFont("Arial", 30, true, false))
                            {
                                // create properties to layout text
                                TextLayoutProperties layoutProperties = new TextLayoutProperties(AnchorType.Center);
                                // draw text
                                drawingEngine.DrawText("Text", font, textBrush, ellipseRect, layoutProperties);
                            }
                        }
                    }
                }
            }

member.value:
            False - the SDK must NOT use special algorithm for drawing sharp image borders;
            True - the SDK must use special algorithm for drawing sharp image borders.

            Default value is false.

member.example:
This C#/VB.NET code shows how to print a multipage TIFF file. Each image will be resized to fill the page.
            
            Public Class Form1
                Inherits System.Windows.Forms.Form
            
                #Region "Fields"
            
                Private _currentPage As Integer
                Private _imageCollection1 As Vintasoft.Imaging.ImageCollection
            
                #End Region
            
            
            
                #Region "Constructors"
            
                Public Sub New()
                    Dim imagePrintDocument1 As New Vintasoft.Imaging.Print.ImagePrintDocument()
                    imagePrintDocument1.PrintScaleMode = Vintasoft.Imaging.Print.PrintScaleMode.BestFit
                    imagePrintDocument1.Center = True
                    AddHandler imagePrintDocument1.PrintImage, New System.EventHandler(Of Vintasoft.Imaging.Print.PrintImageEventArgs)(AddressOf imagePrintDocument1_PrintImage)
            
                    _imageCollection1 = New Vintasoft.Imaging.ImageCollection()
                    _imageCollection1.Add("c:\multipage.tif")
            
                    Dim printDialog1 As New System.Windows.Forms.PrintDialog()
                    printDialog1.Document = imagePrintDocument1
                    If printDialog1.ShowDialog() = System.Windows.Forms.DialogResult.OK Then
                        ' do not use margins
                        Dim pageMargins As New System.Drawing.Printing.Margins(0, 0, 0, 0)
                        imagePrintDocument1.DefaultPageSettings.Margins = pageMargins
            
                        ' print the image
                        imagePrintDocument1.Print()
                    End If
                End Sub
            
                #End Region
            
            
            
                #Region "Methods"
            
                ' The PrintImage event is raised for each page to be printed.
                Private Sub imagePrintDocument1_PrintImage(sender As Object, e As Vintasoft.Imaging.Print.PrintImageEventArgs)
                    If _currentPage < _imageCollection1.Count Then
                        e.Image = _imageCollection1(_currentPage)
            
                        _currentPage += 1
                        If _currentPage >= _imageCollection1.Count Then
                            e.HasMoreImages = False
                            _currentPage = 0
                        Else
                            e.HasMoreImages = True
                        End If
                    End If
                End Sub
            
                #End Region
            
            End Class
            
            
            public class Form1 : System.Windows.Forms.Form
            {
            
                #region Fields
            
                int _currentPage;
                Vintasoft.Imaging.ImageCollection _imageCollection1;
            
                #endregion
            
            
            
                #region Constructors
            
                public Form1()
                {
                    Vintasoft.Imaging.Print.ImagePrintDocument imagePrintDocument1 =
                        new Vintasoft.Imaging.Print.ImagePrintDocument();
                    imagePrintDocument1.PrintScaleMode = Vintasoft.Imaging.Print.PrintScaleMode.BestFit;
                    imagePrintDocument1.Center = true;
                    imagePrintDocument1.PrintImage +=
                        new System.EventHandler<Vintasoft.Imaging.Print.PrintImageEventArgs>(imagePrintDocument1_PrintImage);
            
                    _imageCollection1 = new Vintasoft.Imaging.ImageCollection();
                    _imageCollection1.Add(@"c:\multipage.tif");
            
                    System.Windows.Forms.PrintDialog printDialog1 = new System.Windows.Forms.PrintDialog();
                    printDialog1.Document = imagePrintDocument1;
                    if (printDialog1.ShowDialog() == System.Windows.Forms.DialogResult.OK)
                    {
                        // do not use margins
                        System.Drawing.Printing.Margins pageMargins =
                            new System.Drawing.Printing.Margins(0, 0, 0, 0);
                        imagePrintDocument1.DefaultPageSettings.Margins = pageMargins;
            
                        // print the image
                        imagePrintDocument1.Print();
                    }
                }
            
                #endregion
            
            
            
                #region Methods
            
                // The PrintImage event is raised for each page to be printed.
                void imagePrintDocument1_PrintImage(object sender, Vintasoft.Imaging.Print.PrintImageEventArgs e)
                {
                    if (_currentPage < _imageCollection1.Count)
                    {
                        e.Image = _imageCollection1[_currentPage];
            
                        _currentPage++;
                        if (_currentPage >= _imageCollection1.Count)
                        {
                            e.HasMoreImages = false;
                            _currentPage = 0;
                        }
                        else
                        {
                            e.HasMoreImages = true;
                        }
                    }
                }
            
                #endregion
            
            }

